%change session number before each script runsession = '1';%read and parse codebookcodebookpath = strcat('/Users/ashankbehara/desktop/FIND-Wheels/Session',session); codebook = xlsread(strcat(codebookpath,'/Codebook_Pilot'));codebook = codebook(4:7, 5:25);%read data by trialfor trial = 1:21    %read all trial specific data    path = strcat(codebookpath,'/Trial',trial);    ViconFileName = 'Aditya_20_Vicon.csv';    GENEActivFileName = 'Aditya_20_GA_Wrist.csv';    FPFileName = 'Aditya20_FP_RAWDATA.csv';    Vicondata = xlsread(strcat(path,'/',ViconFileName));    GENEdata = xlsread(strcat(path,'/',GENEActivFileName));    FPdata = xlsread(strcat(path,'/' ,FPFileName));    codebooktrial = codebook(:,trial);        %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%      % TRUNCATING DATA BY START TIME %    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%        hasStartTime = isEmpty(codebooktrial);    if hasStartTime == true        continue;    end    %Vicon Truncation    ViconStartTime = codebooktrial(1,3);    ViconFrames = Vicondata(:,1)';    ColumnStartIndex = 0;    for i = 1:length(ViconFrames)        if ViconFrames(i) == ViconStartTime            ColumnStartIndex = ViconFrames(i);            break        end    end    Vicondata = Vicondata(ColumnStartIndex:end,:);    %GENEActiv Truncation    GENEStartTime = codebooktrial(1,4);    GENETime = GENEdata(:,1)';    ColumnStartIndex = 0;    for i = 1:length(GENETime)        if GENETime(i) == GENEStartTime            ColumnStartIndex = GENETime(i);            break        end    end    GENEdata = GENEdata(ColumnStartIndex:end,:);    %FP Truncation            %end of trial analysisend